import tingle from 'tingle.js';
import { app } from '../main';
import { removeAll } from './helpers';

/**
* Generate an HTML report given some informations on the current state of the
* application (selected region, current study zone, territorial mesh, etc.)
* The generated report also includes a copy of the SVG elements.
*
* @param {Object} sections - The abstract, help and source sections
*   'templated' by the chart component.
* @param {Object} name_my_region - The pretty name of the target region.
* @return {String} - The HTML code of the report, ready to be exported.
*
*/
export function generateHtmlRapport(sections, name_my_region) {
  const {
    section_selection,
    section_help,
    section_source,
    section_optional,
    extra_css,
  } = sections;
  const targetSvgMap = document.getElementById('svg_map').cloneNode(true);
  const targetSvgChart = document.getElementById('svg_bar').cloneNode(true);
  const targetSvgLegend = document.getElementById('svg_legend');
  removeAll(targetSvgMap.querySelectorAll('.brush_map,.brush,.brush_top'));
  removeAll(targetSvgChart.querySelectorAll('image'));
  const serializer = new XMLSerializer();
  const svg_sources = [
    serializer.serializeToString(targetSvgMap),
    serializer.serializeToString(targetSvgChart),
    serializer.serializeToString(targetSvgLegend),
  ];
  svg_sources.forEach((source) => {
    if (!source.match(/^<svg[^>]+xmlns="http:\/\/www\.w3\.org\/2000\/svg"/)) {
      // eslint-disable-next-line no-param-reassign
      source = source.replace(/^<svg/, '<svg xmlns="http://www.w3.org/2000/svg"');
    }
    if (!source.match(/^<svg[^>]+"http:\/\/www\.w3\.org\/1999\/xlink"/)) {
      // eslint-disable-next-line no-param-reassign
      source = source.replace(/^<svg/, '<svg xmlns:xlink="http://www.w3.org/1999/xlink"');
    }
  });
  const new_html_doc = `
<!DOCTYPE html>
<meta charset="utf-8">
<meta name="viewport" content="initial-scale=1.0, maximum-scale=1.0, user-scalable=no" />
<style>
@media print{
  body {
    font-size: 11px;
    margin: 5%;
  }
  div.f1 { width: 100% !important; }
}
@media only screen and (max-width: 959px) {
  body {
    font-size: 11px;
  }
}
@media only screen and (min-width: 960px) and (max-width: 1460px) {
  body {
    font-size: 12px;
  }
}
@media only screen and (min-width: 1461px) {
  body {
    font-size: 14px;
  }
}
body { font-family: sans-serif; }
h1 { color: white; background: #4f81bd; padding: 0.5em; font-family: Monospace,sans-serif; }
h3 { color: #4f81bd; font-family: Monospace,sans-serif; }
div { margin: auto; }
div.f1 { width: 75%; }
div.f2 { display: flex; width: 98%; }
div.footer {
  margin: auto;
  text-align: center;
  width: 80%;
  font-size: 0.8em;
  padding: 10px 5px;
}
p.footer-in { margin: auto; width: 80%; padding-top: 15px; }
${extra_css || ''}
</style>
<title>Regioviz - Rapport ${name_my_region}</title>
<body>
  <div class="f1">
    <h1>Regioviz - Rapport ${name_my_region}</h1>
    <h3>Rappel de la sélection</h3>
    <div>
    ${section_selection}
    </div>
    <h3>Représentations graphiques</h3>
    <div class="f2">
      <div style="width: 37%;">${svg_sources[0]}</div>
      <div style="width: 43%;">${svg_sources[1]}</div>
    </div>
    <div class="f2">
      <div style="width: 37%;">${svg_sources[2]}</div>
      <div style="width: 43%;">${section_optional || ''}</div>
    </div>
    <h3>Aide de lecture</h3>
    <div style="text-align: justify;">${section_help}</div>
    <h3>Sources</h3>
    <div>${section_source}</div>
    <div class="footer">
      <p class="footer-in">
        <i>Ce rapport a été généré par l'application Regioviz, conceptualisée et developpée par l'UMS RIATE en 2018 dans le cadre d'un projet CGET associant les Secrétaires Généraux pour les Affaires Régionales (SGAR).</i>
      </p>
      <img width="80px" style="float:left;" src=" data:image/png;base64,">
      <img width="80px" style="float:right;" src=" data:image/png;base64,">
    </div>
    <div style="clear: both; padding: 10px;"></div>
  </div>
</body>`;
  return new_html_doc;
}

export function makeModalReport() {
  let href;
  const content = `<div id="prep_rapport"><h3>Rapport en cours de préparation...</h3>
<div class="spinner"><div class="cube1"></div><div class="cube2"></div></div></div>`;
  // eslint-disable-next-line new-cap
  const modal = new tingle.modal({
    stickyFooter: false,
    closeMethods: ['overlay', 'escape'],
    closeLabel: 'Close',
    onOpen() {
      document.querySelector('div.tingle-modal.tingle-modal--visible').style.background = 'rgba(0,0,0,0.4)';
      document.querySelector('div.tingle-modal-box').style.width = '40%';
    },
    onClose() {
      modal.destroy();
      URL.revokeObjectURL(href);
    },
  });
  modal.setContent(content);
  modal.open();
  const sections = app.chart.getTemplateHelp();
  const html_doc = generateHtmlRapport(sections, app.current_config.my_region_pretty_name);
  href = URL.createObjectURL(new Blob([html_doc], { type: 'text/html' }));
  setTimeout(() => {
    modal.setContent(`
<div style="text-align: center;margin: auto;padding: 15px;">
  <h3>Export d'un rapport</h3><br><img src="img/thumbnail_report.png" />
</div>
<div style="text-align: center;margin: auto;">
  <p><a class="buttonDownload" id="dl_rapport" download="Regioviz_rapport.html" href="${href}">Télécharger</a></p>
</div>`);
  }, 250);
}
